import React from 'react';
import './new-card-adder.scss';
import { faPlus } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { IReducerState } from '../../reducers/reducer';
import { connect } from 'react-redux';
import { TextInput } from '../inputs/text-input/text-input';
import { TextareaInput } from '../inputs/textarea-input/textarea-input';

interface INewCardAdderState {
  expanded: boolean;
  temporaryCard: any;
}

class NewCardAdder extends React.Component<StateProps, INewCardAdderState> {
  state = {
    expanded: false,
    temporaryCard: null
  };

  render() {
    return (
      <div className={`new-card-adder-container ${this.state.expanded && 'expanded'}`}>
        <div className={'form-content hidden'}>
          <TextInput/>
          <TextareaInput/>
        </div>
        <div className={'create-card-button-container'}>
          <button className={'add-button'}>
            <span>Criar nova tarefa</span>
            <FontAwesomeIcon className={'add-icon'} icon={faPlus}/>
          </button>
        </div>
      </div>
    );
  }
};

const mapStateToProps = (storeState: IReducerState) => ({
  putResponse: storeState.putData,
  deleteResponse: storeState.deleteData
});

type StateProps = ReturnType<typeof mapStateToProps>;

export default connect(mapStateToProps)(NewCardAdder);
