import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React from 'react';
import { ICard } from '../../utils/interfaces';
import './kanban-card.scss';
import { faPen, faTrash, faArrowRight, faArrowLeft, faCheck, faBan } from '@fortawesome/free-solid-svg-icons'
import { ListType } from '../../utils/enums';
import { cloneDeep } from 'lodash';
import { TextInput } from '../inputs/text-input/text-input';
import { TextareaInput } from '../inputs/textarea-input/textarea-input';

interface IKanbanCardProps {
  card: ICard;
}

interface IKanbanCardState {
  temporaryCard: ICard;
  mode: CardMode;
}

enum CardMode {
  VIEW = 'VIEW',
  EDIT = 'EDIT'
}

export class KanbanCard extends React.Component<IKanbanCardProps, IKanbanCardState>  {
  constructor(props: IKanbanCardProps) {
    super(props);

    this.state = {
      temporaryCard: this.props.card,
      mode: CardMode.EDIT
    };
  }

  renderCardView = () => {
    const { card } = this.props;

    return (
      <>
        <div className={'card-header'}>
          <h3 className={'card-title'}>{card.titulo}</h3>
          <div className={'icons-container'}>
            <FontAwesomeIcon className={'card-icon-op delete'} icon={faTrash} />
            <FontAwesomeIcon className={'card-icon-op edit'} onClick={() => this.handleEditPressed()} icon={faPen} />
          </div>
        </div>
        <div className={'card-body'}>
          <div className={'text-container'}>
            <p>{card.conteudo}</p>
          </div>
          <div className={'arrow-buttons-container'}>
            {card.lista !== ListType.TO_DO && <FontAwesomeIcon className={'card-icon-op arrow left'} icon={faArrowLeft} />}
            {card.lista !== ListType.DONE && <FontAwesomeIcon className={'card-icon-op arrow right'} icon={faArrowRight} />}
          </div>
        </div>
      </>
    );
  };

  handleEditPressed = () => {
    this.setState({
      mode: CardMode.EDIT,
      temporaryCard: cloneDeep(this.props.card)
    });
  };

  renderCardEdit = () => {
    const { temporaryCard } = this.state;

    return (
      <>
        <div className={'card-header'}>
          <TextInput
            value={temporaryCard.titulo}
            onChange={event => this.setTemporaryCardValue(event.target.value, 'titulo')}
          />
          <div className={'icons-container'}>
            <FontAwesomeIcon className={'card-icon cancel'} onClick={() => this.cancelEdit()} icon={faBan} />
            <FontAwesomeIcon className={'card-icon confirm'} onClick={() => this.confirmEdit()} icon={faCheck} />
          </div>
        </div>
        <div className={'card-body'}>
          <div className={'text-container'}>
            <TextareaInput
              value={temporaryCard.conteudo}
              onChange={event => this.setTemporaryCardValue(event.target.value, 'conteudo')}
            />
          </div>
        </div>
      </>
    );
  };

  setTemporaryCardValue = (value: string, key: string) => {
    this.setState(prevState => ({
      temporaryCard: {
        ...prevState.temporaryCard,
        [key]: value
      }
    }));
  };

  cancelEdit = () => {
    this.setState({
      mode: CardMode.VIEW
    });
  };

  confirmEdit = () => {};

  render() {
    const { card } = this.props;
    const { mode } = this.state;
    const renderController = {
      VIEW: () => this.renderCardView(),
      EDIT: () => this.renderCardEdit()
    }

    return (
      <div className={`kanban-card-container ${card.lista} ${mode}`}>
        {renderController[mode]()}
      </div>
    );
  }
}
