import { isEmpty, cloneDeep, set, defaultsDeep, unset, get } from 'lodash';
import { getArrayFromSeparatedList, getSeparatedList } from '../store/store';
import { HttpRequestStatus } from './enums';
import { ICard, ICardsList, IReducerListObjects, IRequestData } from './interfaces';

export const updateCardsState = (cards: ICardsList, cardToUpdate: ICard) => {
  const newCards = cards;
  const index = get(newCards, cardToUpdate.lista).findIndex((it: any) => it.id === cardToUpdate.id);
  set(get(newCards, cardToUpdate.lista), index, cardToUpdate);
  return (
    newCards
  );
};

export const createNewCardState = (cards: ICardsList, cardToAdd: ICard) => {
  const newCards = cards;
  const list = get(newCards, cardToAdd.lista);
  list.push(cardToAdd);
  set(newCards, cardToAdd.lista, list);
  return (
    newCards
  );
};

export const updateListsCardState = (cards: ICardsList, cardToAdd: ICard) => {
  const list = getArrayFromSeparatedList(cards);
  console.log(list);
  const index = list.findIndex(it => it.id === cardToAdd.id);
  list[index] = cardToAdd;
  return getSeparatedList(list);
};

export const createNewObject = <DataType extends unknown>(
  entities: IReducerListObjects<DataType>,
  callerKey: string | number,
  reducerFieldObject: IRequestData<DataType>
): IReducerListObjects<DataType> => {
  const entitiesCopy = cloneDeep(entities);
  set(entitiesCopy, callerKey, reducerFieldObject);
  return entitiesCopy;
};

export const updateObjectByKey = <DataType extends unknown>(
  entities: IReducerListObjects<DataType>,
  callerKey: string | number,
  reducerFieldObject: IRequestData<DataType>
): IReducerListObjects<DataType> => {
  if (isEmpty(entities[callerKey])) {
    return createNewObject(entities, callerKey, reducerFieldObject);
  }
  const entitiesCopy = {
    ...cloneDeep(entities),
    [callerKey]: defaultsDeep(reducerFieldObject, entities[callerKey])
  };
  return entitiesCopy;
};

export const resetObjectByKey = <DataType extends unknown>(
  entities: IReducerListObjects<DataType>,
  callerKey: string | number
): IReducerListObjects<DataType> => {
  const entitiesCopy = cloneDeep(entities);
  set(entitiesCopy, callerKey, {
    status: HttpRequestStatus.NOOP,
    data: null
  });
  return entitiesCopy;
};

export const deleteObjectByKey = <DataType extends unknown>(
  entities: IReducerListObjects<DataType>,
  callerKey: string | number
): IReducerListObjects<DataType> => {
  const entitiesCopy = cloneDeep(entities);
  unset(entitiesCopy, callerKey);
  return isEmpty(entitiesCopy) ? {} : entitiesCopy;
}
