import React from 'react';
import { Provider } from 'react-redux';
import './App.scss';
import Kanban from './components/kanban/kanban';
import NewCardAdder from './components/new-card-adder/new-card-adder';
import PreLoader from './components/pre-loader/pre-loader';
import login from './store/login';
import { getStore } from './store/store';
import { toast, ToastContainer, ToastPosition } from 'react-toastify';

const store = getStore();

login(store);

function App() {
  if (localStorage.getItem('jwtToken') == null) return <PreLoader/>

  return (
    <div className="App">
      <div className="app-container">
        <Provider store={store}>
          <NewCardAdder/>
          <Kanban/>
        </Provider>
        <ToastContainer
          position={toast.POSITION.BOTTOM_CENTER as ToastPosition}
          className="toastify-container"
          toastClassName="toastify-toast"
          autoClose={100}
          newestOnTop={false}
          closeOnClick
          rtl={false}
          draggable
          pauseOnHover
        />
      </div>
    </div>
  );
}

export default App;
