import axios from 'axios';
import { get } from 'lodash';
import { Provider } from 'react-redux';
import './App.scss';
import Kanban from './components/kanban/kanban';
import PreLoader from './components/pre-loader/pre-loader';
import { updateCards, updateGetStatus } from './reducers/reducer';
import login from './store/login';
import initStore from './store/store';
import { HttpRequestStatus } from './utils/enums';
import { API_PREFIX, getRequestConfig } from './utils/utils';

const store = initStore();

// if (localStorage.getItem('jwtToken') == null) {
  const response = login(store);
// }

const getCards = () => {
  updateGetStatus(HttpRequestStatus.ONGOING);
  const promise = axios.get(`${API_PREFIX}/cards/`, getRequestConfig());
  promise.then(response => {
    updateGetStatus(HttpRequestStatus.SUCCESS);
    const separatedList: any = {
      ToDo: [],
      Doing: [],
      Done: []
    };

    response.data.forEach((it: any) => {
      separatedList[get(it, 'lista', '')].push(it);
    });

    updateCards(separatedList)
  });
  return null;
};


function App() {
  if (localStorage.getItem('jwtToken') == null) return <PreLoader/>

  return (
    <div className="App">
      <Provider store={store}>
        <Kanban/>
      </Provider>
    </div>
  );
}

export default App;
