import axios from "axios";
import React from "react";
import { connect } from 'react-redux';
import { IReducerState } from "../../reducers/reducer";
import { getCards } from "../../store/store";
import { ListType } from "../../utils/enums";
import { ICard } from "../../utils/interfaces";
import { API_PREFIX, getRequestConfig } from "../../utils/utils";
import KanbanColumn from '../kanban-column/kanban-column';
import './kanban.scss';

interface IKanbanProps extends StateProps {};

class Kanban extends React.Component<IKanbanProps, {}> {
  componentDidMount() {
    getCards();
  }

  test = () => {
    const lista: ICard[] = [
      {
        titulo: 'Título 1', 
        conteudo: 'Mussum Ipsum, cacilds vidis litro abertis. Mauris nec dolor in eros commodo tempor.', 
        lista: ListType.DOING
      },
      {
        titulo : 'Título 2', 
        conteudo: 'Mussum Ipsum, cacilds vidis litro abertis. Mauris nec dolor in eros commodo tempor.', 
        lista: ListType.DOING
      },
      {
        titulo : 'Título 3', 
        conteudo: 'Mussum Ipsum, cacilds vidis litro abertis. Mauris nec dolor in eros commodo tempor.', 
        lista: ListType.DONE
      },
      {
        titulo : 'Título 4', 
        conteudo: 'Mussum Ipsum, cacilds vidis litro abertis. Mauris nec dolor in eros commodo tempor.', 
        lista: ListType.TO_DO
      }
    ];
    lista.map(it => axios.post(`${API_PREFIX}/cards/`, it, getRequestConfig()))
  };

  render() {
    console.log(this.props.store);

    return (
      <div className={'kanban-container'}>
        <button style={{ position: 'fixed', top: '0px' }} onClick={this.test}>vai caralho</button>
        <KanbanColumn type={ListType.TO_DO} />
        <KanbanColumn type={ListType.DOING} />
        <KanbanColumn type={ListType.DONE} />
      </div>
    );
  }
}

const mapStateToProps = (storeState: IReducerState) => ({
  store: storeState
});

type StateProps = ReturnType<typeof mapStateToProps>;

export default connect(mapStateToProps)(Kanban);
