import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React from 'react';
import { ICard } from '../../utils/interfaces';
import './kanban-card.scss';
import { faPen, faTrash, faArrowRight, faArrowLeft } from '@fortawesome/free-solid-svg-icons'
import { ListType } from '../../utils/enums';

interface IKanbanCardProps {
  card: ICard;
}

export class KanbanCard extends React.Component<IKanbanCardProps, {}>  {
  render() {
    const { card } = this.props;

    return (
      <div className={`kanban-card-container ${card.lista}`}>
        <div className={'card-header'}>
          <h3 className={'card-title'}>{card.titulo}</h3>
          <div className={'icons-container'}>
            <FontAwesomeIcon className={'card-icon-op delete'} icon={faTrash} />
            <FontAwesomeIcon className={'card-icon-op edit'} icon={faPen} />
          </div>
        </div>
        <div className={'card-body'}>
          <div className={'text-container'}>
            <p>{card.conteudo}</p>
          </div>
          <div className={'arrow-buttons-container'}>
            {!card.lista === ListType.TO_DO && <FontAwesomeIcon className={'card-icon-op arrow left'} icon={faArrowLeft} />}
            {!card.lista === ListType.DONE && <FontAwesomeIcon className={'card-icon-op arrow right'} icon={faArrowRight} />}
          </div>
        </div>
      </div>
    );
  }
}
