import { IAction, ICard, IRequestData } from '../utils/interfaces';

export const ACTION_TYPES = {
  UPDATE_TOKEN: 'UPDATE_TOKEN'
};

export interface IReducerState {
  cards: {
    TO_DO: ICard[];
    DOING: ICard[];
    DONE: ICard[];
  };
  get: IRequestData<ICard>
  put: IRequestData<ICard>
  post: IRequestData<ICard>
  delete: IRequestData<ICard>
  jwtToken: string;
}

const initialState: IReducerState = {
  cards: {
    TO_DO: [
      { id: '1', titulo: 'TO_DO' }
    ],
    DOING: [
      { id: '2', titulo: 'DOING' }
    ],
    DONE: [
      { id: '3', titulo: 'DONE' }
    ]
  },
  get: {},
  put: {},
  post: {},
  delete: {},
  jwtToken: ''
};

const appReducer = (state: IReducerState = initialState, action: IAction): IReducerState => {
  console.log('action', action);
  switch (action.type) {
    case ACTION_TYPES.UPDATE_TOKEN:
      return {
        ...state,
        jwtToken: action.payload
      }
    default:
      return state;
  }
};

export default appReducer;

export const updateJwtToken = (newToken: string) => {
  console.log(newToken);
  return {
    type: ACTION_TYPES.UPDATE_TOKEN,
    payload: newToken
  };
};
