import axios from "axios";
import { HttpRequestStatus } from "../utils/enums";
import { ICard, ICardsList } from "../utils/interfaces";
import { API_PREFIX, getRequestConfig } from "../utils/utils";
import { updateGetStatus, updateGetData, updateCards } from './reducer';

export const getCards = () => {
  updateGetStatus(HttpRequestStatus.ONGOING);
  const promise = axios.get(`${API_PREFIX}/cards/`, getRequestConfig());
  promise.then(response => {
    updateGetStatus(HttpRequestStatus.SUCCESS);
    updateGetData(response.data);
  });
};

const formatCards = (cards: ICard[]) => {
  const separatedList: ICardsList = {
    ToDo: [],
    Doing: [],
    Done: []
  }

  cards.forEach(it => {
    const index: string = it.lista;
    separatedList[`${index}`].push(it);

  });

  updateCards(separatedList)
};
