import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React from 'react';
import { ICard } from '../../utils/interfaces';
import './kanban-card.scss';
import { faPen, faTrash, faArrowRight, faArrowLeft, faCheck } from '@fortawesome/free-solid-svg-icons'
import { ListType } from '../../utils/enums';
import { cloneDeep } from 'lodash';

interface IKanbanCardProps {
  card: ICard;
}

interface IKanbanCardState {
  temporaryCard: ICard;
  mode: CardMode;
}

enum CardMode {
  VIEW = 'VIEW',
  EDIT = 'EDIT'
}

export class KanbanCard extends React.Component<IKanbanCardProps, IKanbanCardState>  {
  constructor(props: IKanbanCardProps) {
    super(props);

    this.state = {
      temporaryCard: {},
      mode: CardMode.VIEW
    };
  }

  renderCardView = () => {
    const { card } = this.props;

    return (
      <>
        <div className={'card-header'}>
          <h3 className={'card-title'}>{card.titulo}</h3>
          <div className={'icons-container'}>
            <FontAwesomeIcon className={'card-icon-op delete'} icon={faTrash} />
            <FontAwesomeIcon className={'card-icon-op edit'} onClick={() => this.handleEditPressed()} icon={faCheck} />
          </div>
        </div>
        <div className={'card-body'}>
          <div className={'text-container'}>
            <p>{card.conteudo}</p>
          </div>
        </div>
      </>
    );
  };

  handleEditPressed = () => {
    this.setState({
      mode: CardMode.EDIT,
      temporaryCard: cloneDeep(this.props.card)
    });
  };

  renderCardEdit = () => {
    const { temporaryCard } = this.state;

    return (
      <>
        <div className={'card-header'}>
          <h3 className={'card-title'}>{temporaryCard.titulo}</h3>
          <div className={'icons-container'}>
            <FontAwesomeIcon className={'card-icon-op delete'} icon={faTrash} />
            <FontAwesomeIcon className={'card-icon-op edit'} onClick={() => this.handleEditPressed()} icon={faPen} />
          </div>
        </div>
        <div className={'card-body'}>
          <div className={'text-container'}>
            <p>{temporaryCard.conteudo}</p>
          </div>
        </div>
      </>
    );
  };

  render() {
    const { card } = this.props;
    const { mode } = this.state;
    const renderController = {
      VIEW: () => this.renderCardView(),
      EDIT: () => this.renderCardEdit()
    }

    return (
      <div className={`kanban-card-container ${card.lista} ${mode}`}>
        {renderController[mode]}
      </div>
    );
  }
}
